import { Injectable } from "@angular/core";
import { HttpClient, HttpHeaders } from "@angular/common/http";
import * as process from "process";

@Injectable()
export class PageLayoutHttpService {
  constructor(private http: HttpClient) {}
  registration(form: any) {
    const body = {
      query: `

          mutation createUser($input: {email: "createUser222", username: "createUser", password: "createUser", created: 111}) {
            email, username, created
          }

      `,
      operationName: "createUser",
      variables: { input: { ...form, created: +Date.now() } },
    };
    try {
      return this.http.post(`http://localhost:4000/graphql`, body, {
        headers: {
          "Content-Type": "application/json; charset=utf-8",
        },
      });
    } catch (e) {
      console.log(e);
      return e;
    }
  }
}
