import { Component } from "@angular/core";

@Component({
  selector: "app-app-header",
  templateUrl: "./app-header.component.html",
  styleUrls: ["./app-header.component.scss"],
})
export class AppHeaderComponent {
  isDarkMode: boolean = false;
  constructor() {
    // window.matchMedia('(prefers-color-scheme: dark)')
    window
      .matchMedia("(prefers-color-scheme)")
      .addEventListener("change", (event) => {
        console.log(event);
      });
  }
  changeMode() {
    console.log(this.isDarkMode);
  }
}
<div class="app-header">
  <div class="container app-header__container">
    <a mat-button routerLink="/">Mall</a>
    <div class="app-header__navigation">
      <mat-slide-toggle color="primary" [checked]="isDarkMode" [(ngModel)]="isDarkMode" (ngModelChange)="changeMode()">
        Ночной режим
      </mat-slide-toggle>
      <button mat-flat-button color="primary" class="app-header__navigation_btn">
        <mat-icon aria-hidden="false">person_outline</mat-icon>
        Войти
      </button>
      <button
        mat-stroked-button
        color="primary"
        class="app-header__navigation_btn"
        matBadge="15"
        matBadgeColor="warn"
      >
        <mat-icon aria-hidden="false">favorite_border</mat-icon>
        Избранное
      </button>
      <button
        mat-stroked-button
        color="primary"
        class="app-header__navigation_btn"
        matBadge="25"
        matBadgeColor="warn"
      >
        <mat-icon aria-hidden="false">shopping_cart</mat-icon>
        Корзина
      </button>
    </div>
  </div>
</div>
